package multidecl

// Multiple variables in one line
var VarA, VarB = "value A", "value B"

// Multiple variables in one var block
var (
	VarC = "value C"
	VarD = "value D"
)

// Multiple constants in one line
const ConstA, ConstB = "const A", "const B"

// Multiple constants in one const block
const (
	ConstC = "const C"
	ConstD = "const D"
)
